import React, {useState, useEffect} from "react";
import "../styles.css";
import Loader from "./Loader";
import Button from "./Button";

function Home() {
    const [isLoading, setIsLoading] = useState(true); // Track loading state

    // Simulate a loading delay
    useEffect(() => {
      const timer = setTimeout(() => {
        setIsLoading(false); // Stop loading after 3 seconds
      }, 3000);

      return () => clearTimeout(timer); // Clean up the timer
    }, []);

    return (

      <div>
        {isLoading ? (
          <Loader /> // Show the loader when `isLoading` is true
        ) : (
          <div >
            <h1 style={{textAlign: "left"}}>Hi<br /> I'm Levent , <br /> software engineer.</h1>
            <p style={{fontFamily: "Poppins, sans-serif", fontWeight: '450'}}>Computer Science @ Univeristy of Houston</p>
            <contactButton />


          </div>
        )}
      </div>
    );
  }



export default Home;
