import React, {useState, useEffect} from "react";
import Loader from "./Loader";

function About() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a loading time
    const timer = setTimeout(() => setLoading(false), 3000); // 3 seconds delay
    return () => clearTimeout(timer); // Cleanup
  }, []);

  if (loading) {
    return <Loader />; // Show Loader while loading
  }
  return (
    <div className="about">
      <h1>About Me</h1>
      <p>
        I'm Levent, a software engineering student with a strong passion for coding, 
        problem-solving, and designing intuitive user interfaces. I love learning new tools 
        and building projects that make an impact.
      </p>
    </div>
  );
}

export default About;
