import React, { useState } from "react";
import "./Header.css";

function Header({ text }) {

    const [hoverStates, setHoverStates] = useState(
        Array(text.length).fill(false) // Initial state: all characters not hovered
      );

      function isMouseOver(index) {
        setHoverStates((prev) =>
          prev.map((hover, i) => (i === index ? true : hover))
        );
      }

      function isMouseOut(index) {
        setHoverStates((prev) =>
          prev.map((hover, i) => (i === index ? false : hover))
        );
      }

    return (
      <h1 className="header">
        {text.split("").map((char, index) => (
          <span onMouseOver={isMouseOver} onMouseOut={isMouseOut} key={index} className="jelly-char">
            {char === " " ? "\u00A0" : char} {/* Replace spaces with non-breaking spaces */}
          </span>
        ))}
      </h1>
    );
  }


export default Header;
