import React from "react";
import LinkedInPhoto from "../components/Linkedin_Profile.jpg";
import Loader from "./Loader";

function Home() {
    const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a loading delay (e.g., API fetch, assets loading)
    const timer = setTimeout(() => setLoading(false), 3000); // 3 seconds

    // Cleanup the timer
    return () => clearTimeout(timer);
  }, []);

  if (loading) {
    return <Loader />; // Show the loader while loading is true
  }

  return (
    <div className="home">
      <h1>Hi, <br /> I'm Levent! <br /> A passionate software developer and designer.</h1>
      <img style={{width: "300px", height: "400px"}}src={LinkedInPhoto}/>
    </div>
  );
}

export default Home;
