import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import Loader from "./Loader";


function App() {
  const [isLoading, setIsLoading] = useState(false);

  // Simulate loading during page transitions
  const handleNavigation = () => {
    setIsLoading(true);
    setTimeout(() => setIsLoading(false), 1000); // Simulate loading delay
  };

  return (
    <Router>
      <div>
        {/* Loader */}
        {isLoading && <Loader />}

        {/* Navigation Links */}
        <nav>
          <a href="/" onClick={handleNavigation}>
            Home
          </a>
          <a href="/about" onClick={handleNavigation}>
            About
          </a>
          <a href="/projects" onClick={handleNavigation}>
            Projects
          </a>
          <a href="/skills" onClick={handleNavigation}>
            Skills
          </a>
        </nav>

        {/* Page Content */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/skills" element={<Skills />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
