import React from 'react';
import styled from 'styled-components';

const Card = (props) => {
  return (
    <StyledWrapper>
      <div className="card" style={{backgroundImage: `url(${props.photo})`}}>
        <button className="card-button">{props.text}</button>
      </div>
    </StyledWrapper>
  );
}

const StyledWrapper = styled.div`
  .card-container {
    display: flex;
    flex-wrap: wrap; /* Allows wrapping on smaller screens */
    justify-content: space-around; /* Spreads the cards evenly */
    align-items: flex-start; /* Aligns the top of the cards */
    padding: 20px; /* Adds padding around the container */
    margin: 0 auto; /* Centers the entire card container */
    max-width: 1200px; /* Keeps the cards from stretching too wide */
  }

  .card-title {
    font-size: 1.2em;
    font-weight: bold;
    color: #5a189a; /* Purple color for the title */
    margin-bottom: 10px; /* Add some spacing between title and the card */
    text-align: center; /* Center-align the text */
  }

  .card {
    width: 190px;
    height: 254px;
    border-radius: 20px;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    background-color: #f5f5f5;
    position: relative;
    padding: 1rem;
    border: 2px solid #c3c6ce;
    transition: 0.5s ease-out;
    overflow: hidden;
    display: flex; /* Flexbox layout for vertical alignment */
    flex-direction: column; /* Stack items vertically */
    justify-content: space-between; /* Push title and button apart */
    align-items: center; /* Center items horizontally */
    margin: 0 15px; /* Adds horizontal spacing between cards */
  }

  .card-button {
    width: 100px;
    height: 40px;
    border-radius: 1rem;
    border: none;
    background-color: #008bf8;
    color: white;
    font-size: 0.9rem;
    font-weight: bold;
    margin-top: auto; /* Pushes the button to the bottom */
    padding: 0.5rem;
    cursor: pointer;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
    transition: all 0.3s ease-out;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
  }

  .card-button:hover {
    background-color: #006bbd;
    transform: translateY(-5px);
    box-shadow: 0 6px 15px rgba(0, 0, 0, 0.3);
  }`;

export default Card;
