import React, {useState, useEffect} from "react";
import Loader from "./Loader";
import Header from "./Header";
import "./About.css";

function Contact() {
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Simulate a loading time
        const timer = setTimeout(() => setLoading(false), 2000); // 3 seconds delay
        return () => clearTimeout(timer); // Cleanup
    }, []);

    if (loading) {
        return <Loader />; // Show Loader while loading
    }
    return (
        <div className="about">
            <Header text="Contact Me" />
            <p>Thank you for your interest in getting in touch!<br/>
            I value open communication and welcome any inquiries, 
            feedback, or collaboration opportunities. 
            Please don't hesitate to get in touch with me by filling out the contact form.
            </p>
        </div>
    )
}

export default Contact;
