import React, {useState, useEffect} from "react";
import {motion} from "framer-motion";
import LinkedInPhoto from "../components/Linkedin_Profile.jpg";
import Loader from "./Loader";


function Home() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate a loading time
    const timer = setTimeout(() => setLoading(false), 3000); // 3 seconds delay
    return () => clearTimeout(timer); // Cleanup
  }, []);

  if (loading) {
    return <Loader />; // Show Loader while loading
  }
  return (
    <div className="home">
       <motion.div
        initial={{ opacity: 0, y: 20 }} // Start invisible and slightly below
        animate={{ opacity: 1, y: 0 }}  // Fade in and move up
        exit={{ opacity: 0, y: -20 }}   // Fade out and move up on exit
        transition={{ duration: 1 }}  // Duration of the animation
      />
      <h1>Hi, <br /> I'm Levent! <br /> A passionate software developer and designer.</h1>
      <img style={{width: "300px", height: "400px"}}src={LinkedInPhoto}/>
    </div>
  );
}

export default Home;
